set(plot1(2),'Color',[0.749019622802734 0 0.749019622802734],...
    'DisplayName','RK2 TVD');

% Create plot
plot(X3,Y2,'Parent',axes1,'LineWidth',2,...
    'Color',[0.682352960109711 0.466666668653488 0],...
    'DisplayName',['Backward Euler ',sprintf('\n'),'\Delta t = O(\Delta x)']);

% Create xlabel
xlabel('x','FontWeight','bold','FontSize',16);

% Create ylabel
ylabel('p(t, x = 0.12)','FontWeight','bold','FontSize',16);

% Create title
title('Solutions with harmonic averaging and various temporal schemes',...
    'FontWeight','bold',...
    'FontSize',16);

% Create legend
legend1 = legend(axes1,'show');
set(legend1,'Location','SouthEast');

